mapper.select:
SELECT * FROM ADMIN
WHERE ADMIN_ID = #{adminId}
AND ADMIN_PASS = #{adminPass}

SELECT c.* FROM ( SELECT ROWNUM RNUM, a.* FROM ( SELECT * FROM MEMBER WHERE DEL = 'N'
AND mem_id LIKE '%' || #{searchKeyword} || '%'
AND mem_name LIKE '%' || #{searchKeyword} || '%'
AND mem_type LIKE '%' || #{searchKeyword} || '%'
) a WHERE ROWNUM <= #{lastIndex} ) c WHERE RNUM > #{firstIndex}

mapper.update:

UPDATE member 
	set del = 'Y' 
where mem_id = #{memId}

mapper.select:
SELECT c.* FROM ( SELECT ROWNUM RNUM, a.* FROM ( SELECT * FROM PROJECT WHERE DEL = 'N' AND pro_name != 'null'
AND pro_name LIKE '%' || #{searchKeyword} || '%'
AND mem_id LIKE '%' || #{searchKeyword} || '%'
) a WHERE ROWNUM <= #{lastIndex} ) c WHERE c.RNUM > #{firstIndex}

mapper.update:
UPDATE PROJECT
	SET DEL = 'Y'
WHERE REQ_ID = #{reqId}
