mapper.select:
SELECT * FROM( SELECT ROWNUM rnum, a.* FROM (SELECT b.* FROM alarm b WHERE mem_id = #{memId}
AND alarm_type = 'req-pl' OR alarm_type = 'res-pl'
AND alarm_type = 'reply'
AND alarm_type = 'posts'
ORDER BY to_number(b.alarm_id)desc) a
WHERE rownum <= #{lastIndex}) WHERE rnum > #{firstIndex}


    	SELECT COUNT(*)
		  FROM alarm
		 WHERE mem_id = #{memId}

mapper.update:
UPDATE alarm
   SET alarm_status = 'Y'
 WHERE alarm_Id = #{alarmId}
