mapper.select:
	 SELECT * 
   FROM publicfile 
   WHERE some_id = #{someId} and category_id = #{categoryId} and req_id = #{reqId}

  	SELECT Z.*
	      FROM (
	      	SELECT ROWNUM RNUM , 
	           	   S.*
		      FROM (				  	
					SELECT a.PUB_ID, a.PUB_EXTENSION ,a.pub_filename, to_char(a.reg_dt,'yyyy-mm-dd hh:mm') as reg_dt ,
						   a.pub_size , b.mem_name as mem_id
					  FROM publicfile a, member b
					 WHERE req_id = #{reqId}
					   AND a.mem_id = b.mem_id




								AND	a.pub_filename LIKE '%' || #{searchKeyword} || '%'


								AND b.mem_name LIKE '%' || #{searchKeyword} || '%'






								AND 1 = 1


								AND  pub_extension not in('png', 'jpg','gif', 'xlsx', 'pptx', 'txt')


								AND pub_extension = #{pubExtension}





				     ORDER BY reg_dt desc
			     ) S

			 WHERE ROWNUM  <=  ${lastIndex} 

      ) Z
     WHERE Z.RNUM > #{firstIndex}
