SELECT c.* FROM ( SELECT ROWNUM rnum, a.* FROM (SELECT * from (SELECT hotissue.hissue_id, hotissue.hissuet_cont, lpad(' ', (level - 1) * 4)|| hissue_title as hissue_title, TO_CHAR(hotissue.reg_dt,'yyyy-mm-dd') as reg_Dt, hotissue.req_id, hotissue.hissue_parentid, hotissue.hissue_level, hotissue.del, member.mem_name as writer FROM( select * from member)member, hotissue hotissue WHERE member.mem_id = hotissue.writer AND hotissue.del = 'N' AND hotissue.req_id = #{reqId}
AND hotissue.mem_name LIKE '%' || #{searchKeyword} || '%'
AND hotissue.hissue_title LIKE '%' || #{searchKeyword} || '%'
AND hotissue.hissuet_cont LIKE '%' || #{searchKeyword} || '%'
start with hotissue.hissue_parentid is null CONNECT BY PRIOR HISSUE_ID = HISSUE_PARENTID order siblings by HISSUE_ID desc))a WHERE rownum <= #{lastIndex}) c WHERE rnum > #{firstIndex}
