mapper.select:
SELECT Z.* FROM ( SELECT ROWNUM RNUM , S.* FROM ( select c.issue_id , c.issue_title , c.issue_cont , to_char(c.reg_dt,'YYYY-MM-DD hh:mm') as reg_dt , c.issue_kind, c.book_id as issue_del , d.mem_name as mem_id from (select a.issue_id , a.issue_title , a.issue_cont , a.reg_dt , a.issue_kind, b.book_id , a.mem_id , a.issue_del from issue a left outer join bookmark b on a.issue_id = b.issue_id) c , member d where d.mem_id = c.mem_id and c.issue_del = 'N'
AND c.mem_id LIKE '%' || #{searchKeyword} || '%'
AND c.issue_title LIKE '%' || #{searchKeyword} || '%'
AND c.issue_cont LIKE '%' || #{searchKeyword} || '%'
AND c.issue_kind = #{issueKind}
ORDER BY c.reg_dt desc ) S
WHERE ROWNUM <= ${lastIndex}
) Z WHERE Z.RNUM > #{firstIndex}

mapper.insert:

INSERT INTO issue (
    issue_id, issue_title, issue_cont,  reg_dt,  category_id,  
    issue_kind, issue_del, req_id, mem_id, todo_id
) VALUES (
    #{issueId}, #{issueTitle}, #{issueCont}, sysdate,  #{categoryId}, 
    #{issueKind}, 'N', #{reqId} , #{memId}, #{todoId}








)

mapper.update:
UPDATE issue
issue_title=#{issueTitle}, issue_cont=#{issueCont}, issue_kind=#{issueKind}, category_id=#{categoryId}, reg_dt=sysdate, mem_id=#{memId}, issue_del=#{issueDel}, todo_id=#{todoId}, req_id=#{reqId}
where issue_id = #{issueId}


UPDATE issue 
	set issue_del = 'Y' 
where issue_id = #{issueId}
