mapper.select:
SELECT c.* FROM ( SELECT ROWNUM RNUM , S.* FROM ( select a.issue_id , a.issue_title , a.issue_cont , to_char(a.reg_dt,'YYYY-MM-DD') as reg_dt , a.issue_kind, b.mem_name as mem_id from( select issue_id, issue_title, issue_cont, reg_dt, issue_del, mem_id, issue_kind from issue where req_id = #{reqId}) a , member b where a.mem_id = b.mem_id and a.issue_del = 'N'
AND a.mem_id LIKE '%' || #{searchKeyword} || '%'
AND a.issue_title LIKE '%' || #{searchKeyword} || '%'
AND a.issue_cont LIKE '%' || #{searchKeyword} || '%'
AND a.issue_kind = #{issueKind}
ORDER BY a.reg_dt desc ) S
WHERE ROWNUM <= ${lastIndex}
) c WHERE c.RNUM > #{firstIndex}

mapper.insert:

	SELECT  ISSUE_SEQ.nextval FROM DUAL


INSERT INTO issue (
    issue_id, issue_title, issue_cont,  reg_dt,  category_id,  
    issue_kind, issue_del, req_id, mem_id, todo_id
) VALUES (
    #{issueId}, #{issueTitle}, #{issueCont}, sysdate,  #{categoryId}, 
    #{issueKind}, 'N', #{reqId} , #{memId}, #{todoId}


)

mapper.update:
UPDATE issue
issue_title=#{issueTitle}, issue_cont=#{issueCont}, issue_kind=#{issueKind}, category_id=#{categoryId}, reg_dt=sysdate, mem_id=#{memId}, issue_del=#{issueDel}, todo_id=#{todoId}, req_id=#{reqId}
where issue_id = #{issueId}


UPDATE issue 
	set issue_del = 'Y' 
where issue_id = #{issueId}
