mapper.select:
SELECT COUNT(*)
  FROM privatefile
 WHERE MEM_ID = #{memId}

mapper.insert:
INSERT INTO PRIVATEFILE ( PRIV_ID, PRIV_FILEPATH, PRIV_FILENAME, PRIV_SIZE, REG_DT, MEM_ID ) SELECT PRIV_SEQ.NEXTVAL as PRIV_ID, T.* FROM(
SELECT #{item.privFilepath} as PRIV_FILEPATH , #{item.privFilename} as PRIV_FILENAME , #{item.privSize} as PRIV_SIZE , TO_DATE(SYSDATE,'YYYY/MM/DD') as REG_DT , #{item.memId} as MEM_ID FROM dual
) T
