mapper.select:
SELECT * FROM( SELECT ROWNUM rnum, a.* FROM (SELECT b.req_id, b.req_title, b.req_period, b.req_filepath, b.req_filename, b.mem_id, b.status, b.pl_id, m.mem_name as pl_name, p.pro_percent, p.req_id as pro_id FROM (SELECT * FROM reqlist WHERE mem_id = #{memId}
AND req_title LIKE '%' || #{searchKeyword} || '%'
AND req_period LIKE '%' || #{searchKeyword} || '%'
AND pl_Id LIKE '%' || #{searchKeyword} || '%'
AND status LIKE '%' || #{searchKeyword} || '%'
) b LEFT OUTER JOIN project p ON b.req_id = p.req_id LEFT OUTER JOIN member m ON b.pl_id = m.mem_id ORDER BY TO_NUMBER(b.req_id)DESC) a
WHERE rownum <= #{lastIndex}) WHERE rnum > #{firstIndex}

mapper.insert:
INSERT INTO REQLIST
	(REQ_ID
	 ,REQ_TITLE
	 ,REQ_CONT
	 ,REQ_PERIOD
	 ,REQ_FILEPATH
	 ,REQ_FILENAME
	 ,MEM_ID
	 ,STATUS
	 ,PL_ID)
VALUES(REQ_SEQ.nextval
	 ,#{reqTitle}
	 ,#{reqCont}
	 ,#{reqPeriod}
	 ,#{reqFilepath, jdbcType=VARCHAR}
	 ,#{reqFilename, jdbcType=VARCHAR}
	 ,#{memId}
	 ,#{status, jdbcType=VARCHAR}
	 ,#{plId, jdbcType=VARCHAR})

mapper.update:
UPDATE reqlist
req_title = #{reqTitle}, req_cont = #{reqCont}, req_period = #{reqPeriod}, req_filepath = #{reqFilepath}, req_filename = #{reqFilename}, pl_id = #{plId}, status = #{status}
WHERE 1=1 AND req_id = #{reqId}

UPDATE   reqlist
   SET   pl_id = null   
 WHERE   req_id = #{reqId}
