mapper.select:
SELECT c.* FROM ( SELECT ROWNUM rnum, a.* FROM (select * from (SELECT todo.todo_id, ABS(TO_DATE(TO_CHAR(todo.todo_start, 'YYYYMMDD')) - To_date(to_char(SYSDATE,'yyyymmdd'))) as todo_start, TO_DATE(TO_CHAR(SYSDATE, 'yyyymmdd')) - To_date(to_char(todo.todo_end,'yyyymmdd')) as todo_end, todo.todo_cont, todo_title, todo.todo_percent, todo.todo_importance, todo.todo_level, todo.req_id, todo.todo_parentid, todo.del, member.mem_name AS mem_id FROM( select * from member)member, todo todo WHERE member.mem_id = todo.mem_id AND todo.del = 'N' AND todo.req_id = #{reqId} AND member.mem_id = #{memId}
AND member.mem_name LIKE '%' || #{searchKeyword} || '%'
AND todo.todo_cont LIKE '%' || #{searchKeyword} || '%'
AND todo.todo_title LIKE '%' || #{searchKeyword} || '%'
order by todo_id desc))a WHERE rownum <= #{lastIndex}) c WHERE rnum > #{firstIndex}


SELECT COUNT(*)
	FROM todo
	WHERE req_id = #{reqId}
	AND mem_id= #{memId}
	AND del = 'N'

mapper.update:
UPDATE todo
SET
	todo_percent =#{todoPercent}
WHERE
	todo_id = #{todoId}
